function loadDarkModeToggle(){let e=document.getElementById("toggle-dark-mode"),o=document.body,t="user-dark-mode",d=localStorage.getItem(t);"true"===d&&o.classList.add("dark-mode"),e.addEventListener("click",()=>{o.classList.toggle("dark-mode");let d=o.classList.contains("dark-mode");localStorage.setItem(t,d),e.textContent=d?"Modo Claro":"Modo Escuro"})}export{loadDarkModeToggle};