function handleRouting(){let e=window.location.hash||"#home",t={"#home":"home-view","#projetos":"projetos-view","#cadastro":"cadastro-view"}[e];if(!t)return;document.querySelectorAll(".page-view").forEach(e=>{e.classList.add("hidden"),e.classList.remove("active")});let a=document.getElementById(t);a&&(a.classList.remove("hidden"),a.classList.add("active"))}window.addEventListener("load",handleRouting),window.addEventListener("hashchange",handleRouting);export{handleRouting};